mapper.select:
SELECT msg_id,msg_info,name,phone,email,address,create_time
FROM tbl_guest_book

	AND msg_id = #{msg_id,jdbcType=VARCHAR}
	AND msg_info = #{msg_info,jdbcType=VARCHAR}
	AND name = #{name,jdbcType=VARCHAR}
	AND phone = #{phone,jdbcType=VARCHAR}
	AND email = #{email,jdbcType=VARCHAR}
	AND address = #{address,jdbcType=VARCHAR}
	AND create_time = #{create_time,jdbcType=VARCHAR}


order by ${b_order_name} ${b_order_asc}

mapper.insert:
INSERT INTO tbl_guest_book (msg_id,msg_info,name,phone,email,address,create_time)

	SELECT #{item.msg_id,jdbcType=VARCHAR},#{item.msg_info,jdbcType=VARCHAR},#{item.name,jdbcType=VARCHAR},#{item.phone,jdbcType=VARCHAR},#{item.email,jdbcType=VARCHAR},#{item.address,jdbcType=VARCHAR},#{item.create_time,jdbcType=VARCHAR}
	FROM dual

mapper.update:
UPDATE tbl_guest_book
msg_id = #{msg_id,jdbcType=VARCHAR}, msg_info = #{msg_info,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, address = #{address,jdbcType=VARCHAR}, create_time = #{create_time,jdbcType=VARCHAR},
WHERE msg_id = #{msg_id,jdbcType=VARCHAR}
