mapper.select:
SELECT province_code,province_name
FROM tbl_province_info

	AND province_code = #{province_code,jdbcType=VARCHAR}
	AND province_name = #{province_name,jdbcType=VARCHAR}


order by ${b_order_name} ${b_order_asc}

mapper.insert:
INSERT INTO tbl_province_info (province_code,province_name)

	SELECT #{item.province_code,jdbcType=VARCHAR},#{item.province_name,jdbcType=VARCHAR}
	FROM dual

mapper.update:
UPDATE tbl_province_info
province_code = #{province_code,jdbcType=VARCHAR}, province_name = #{province_name,jdbcType=VARCHAR},
WHERE province_code = #{province_code,jdbcType=VARCHAR}
